    /*
     * Listing of tbl_faq
     */
    function index()
    {
        $data['tbl_faq'] = $this->Tbl_faq_model->get_all_tbl_faq();
        
        $data['_view'] = 'tbl_faq/index';
        $this->load->view('layouts/main',$data);
