    {
        parent::__construct();
    }
    
    /*
     * Get tbl_faq by faq_id
     */
    function get_tbl_faq($faq_id)
    {
        return $this->db->get_where('tbl_faq',array('faq_id'=>$faq_id))->row_array();
    }
        
    /*
     * Get all tbl_faq
     */
    function get_all_tbl_faq()
    {
        $this->db->order_by('faq_id', 'desc');
