    }
    
    /*
     * function to update tbl_faq
     */
    function update_tbl_faq($faq_id,$params)
    {
        $this->db->where('faq_id',$faq_id);
        return $this->db->update('tbl_faq',$params);
    }
    
    /*
     * function to delete tbl_faq
     */
    function delete_tbl_faq($faq_id)
    {
        return $this->db->delete('tbl_faq',array('faq_id'=>$faq_id));
    }
